package main

import (
	"fmt"
	"strconv"
	"strings"
)

func clamp(x, low, high int) int {
	if x < low {
		return low
	} else if x > high {
		return high
	}
	return x
}

type V2 struct{ X, Y int }

func (z *V2) Do(dir rune) {
	switch dir {
	case 'U':
		z.Y = clamp(z.Y-1, 0, 2)
	case 'D':
		z.Y = clamp(z.Y+1, 0, 2)
	case 'L':
		z.X = clamp(z.X-1, 0, 2)
	case 'R':
		z.X = clamp(z.X+1, 0, 2)
	}
}

func (z *V2) Value() string { return strconv.Itoa(z.Y*3 + z.X + 1) }

func Run(input string) string {
	r := ""

	var pos V2
	for _, line := range strings.Split(input, "\n") {
		for _, r := range line {
			pos.Do(r)
		}
		r += pos.Value()
	}

	return r
}

func main() {
	fmt.Println(Run(`ULL
RRDDD
LURDL
UUUUD`))

	fmt.Println(Run(`



`))
}
